import React, { useEffect, useRef } from 'react';
import { BrowserRouter as Router,Switch,Route } from 'react-router-dom';
import './assets/css/reset.css'
import Home from './components/Home';
import Profile from './components/Profile';
import Header from './components/Header';
import MessageBox from './components/MessageBox';
import Explore from './components/Explore';
import '../node_modules/bootstrap/dist/css/bootstrap.css'
import {IoIosConstruct} from 'react-icons/io';
import {gsap} from "gsap"

function App() {

    const overlayRef = useRef();
    const overlayTitleRef = useRef();
    let time = 0 ;

   setTimeout(() => {
    if (time <= 100) {
        time++;
        let loader = document.querySelector('.loader');
        loader.innerHTML = time
    }
   }, 1000);
   console.log(time)
    useEffect(() => {
        gsap.to(overlayRef.current, {
            duration : 3,
            y : 100,
            ease : "Power1.easeInOut",
            scale : 2,
            rotate : 360
        })
        gsap.to(overlayTitleRef.current,{
            duration : 3,
            y : -50,
            rotate : 360
        })
    })

    const headerCss = {
        width : "100%",
        background : "#fff",
        position : "fixed",
        top : "0",
        left : "0",
        borderBottom : "1px solid rgba(var(--b6a,219,219,219),1)"
    }

    return ( 
      <Router>
          <div className="overlay">
              <p ref={overlayRef}><IoIosConstruct size="150" color="#fff"/></p>
              <p ref={overlayTitleRef}><span className="overlay-title">Proje Yapım Aşamasında</span></p>
              <p className="loader"></p>
          </div>
          <section style={headerCss}>
                    <Header/>  
          </section> 
          <Switch>
                <Route path="/" exact component={Home}/>
                <Route path="/username" exact component={Profile}/>
                <Route path="/message" exact component={MessageBox}/>
                <Route path="/explore" exact component={Explore}/>
          </Switch>
      </Router>
    )
}

export default App;
