import React, { useEffect, useState } from 'react'

function UyeDetay({match}) {
        
    const [uye,setUye] = useState([])

    useEffect(() => {
        getUser()
    },[])

    const getUser = async () => {
        const data = await fetch(`https://jsonplaceholder.typicode.com/users/${match.params.id}`);
        const user = await data.json(data)
        setUye(user)
    }

    const updateUser = {
        method : "PUT",
        headers : {'Content/Type' : 'application/json'},
        body : JSON.stringify({name : 'yemlihan'})
    }

    const up = () => {
        fetch(`https://jsonplaceholder.typicode.com/users/${match.params.id}`,updateUser)
        .then(res => res.json())
        .then(data => setUye({name : data.name}))
        console.log(setUye())
    }

    return (
        <div>
            <h5>{uye.name}</h5>
        </div>
    )
}

export default UyeDetay
