import React from 'react'
import '../assets/css/profile.css'
import {IoIosSettings} from 'react-icons/io'
import {AiOutlineTable,AiOutlineCodeSandbox,AiOutlineSave} from 'react-icons/ai'
import {BiUserPin} from 'react-icons/bi'
import {gsap} from "gsap"
import {useState, useEffect, useRef } from 'react';

export default function Profile() {

    const overlayRef = useRef();
    const overlayTitleRef = useRef();
    let time = 0 ;
      
    setInterval(timer,20);
    clearInterval(clearTime);

    function timer (){
        let loader = document.querySelector('.loader');
        let body = document.querySelector('body');
        if (time <= 99) {
            time++;
            body.style.overflowY = "hidden";
            loader.innerHTML = "%" + time;
        }
        else if(time == 100){
            setTimeout(() => {
                gsap.to(".overlay",{
                    duration :2 ,
                    y : -1000,
                    ease : "Power2.easeOut",
                    autoAlpha : 0
                })
                body.style.overflowY = "auto";
            }, 2000);
        }
    }
    useEffect(() => {
        gsap.to(overlayRef.current, {
            duration : 3,
            y : 100,
            ease : "Power1.easeInOut",
            scale : 2,
            rotate : 360
        })
        gsap.to(overlayTitleRef.current,{
            duration : 3,
            y : 100,
            rotate : 360
        })
    })
    function clearTime(){
        let loader = document.querySelector('.loader');
            if (time == 100) {
                time = 100;
                loader.innerHTML = 100;
            }
    }
    
    return (
        <div>
             <section style={{marginTop: "85px"}}>
                <div class="container">
                    <div class="main d-flex flex-column w-100">
                        <div className="profile-info d-flex">
                                <div className="profile-icon">
                                    <img width="150" height="150" className="rounded-circle" src="https://play-lh.googleusercontent.com/GNF5wSF3XNLIZv0iMajukuMjz23E7XiQtgyJNI6P0ZeGMBYT8fonyv8u_q1Qwpmj3g" alt="" />
                                </div>
                                <section>
                                    <div className="profile-all-infos d-flex flex-column w-100">
                                        <div className="profile-settings mb-4 d-flex">
                                            <span>username</span>
                                            <div className="profile-edit ps-4">
                                                <button>Profili Düzenle</button>
                                            </div>
                                            <div className="profile-setting-icon ps-3">
                                                <IoIosSettings size="30"/>
                                            </div>
                                        </div>
                                        <div className="profile-followers d-flex">
                                            <ul className="list-style-none">
                                                <li><span className="span-counter">15</span> gönderi</li>
                                                <li><span className="span-counter">378</span> takipçi</li>
                                                <li><span className="span-counter">241</span> takip</li>
                                            </ul>
                                        </div>
                                        <div className="profile-username w-100 d-flex">
                                            <span className="span-counter">Name Surname</span>
                                        </div>
                                    </div>
                                </section>
                        </div>
                        <div className="profile-collapse">
                            <ul className="tab p-0 d-flex w-100 justify-content-center align-items-center">
                                <li id="posts"><AiOutlineTable/> gönderiler</li>
                                <li id="ig-tv"><AiOutlineCodeSandbox/> ig tv</li>
                                <li id="recorded"><AiOutlineSave/> kaydedilenler</li>
                                <li id="tagged"><BiUserPin/> etiketlenenler</li>
                            </ul>
                        </div>
                        <div className="profile-show-box">
                            <div id="posts" className="active">
                                post
                            </div>
                            <div id="ig-tv">
                                ig
                            </div>
                            <div id="recorded">
                                recorded
                            </div>
                            <div id="tagged">
                                tagged
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    )
}
