import React, {useRef, useState, useEffect} from 'react'
import db, { auth, provider } from '../firebase';
import { collection, addDoc, getDocs, setDoc } from "firebase/firestore"; 
import { getAuth, signInWithPopup, GoogleAuthProvider} from "firebase/auth";



export default function Signup() {

    const [name,setName] = useState("");
    const [password,setPassword] = useState("");
    const [user,setUser] = useState(null);

    const add = (e) => {
        e.preventDefault()
            // add db 
            // try {
            //     const docRef =  addDoc(collection(db, "users"), {
            //     username: name,
            //     password: password,
            //     });
            // } catch (err) {
            //     console.error("Error adding document: ", err);
            // }



            // get db
            const getTable =  getDocs(collection(db, "users"));
            const jsonTable = getDocs(getTable) 

            if(jsonTable){
                console.log(jsonTable.data())
            }

    }

    useEffect(() => {
        getAuth().onAuthStateChanged(authUser => {
            if(authUser){
                setUser(authUser)
            }else{
                setUser(null)
            }
        }) 
    }, [])

    const login = () => {
        const auth = getAuth();
        signInWithPopup(auth,provider)
        .then((result) => {
            const credential = GoogleAuthProvider.credentialFromResult(result);
            const token = credential.accessToken;
            const user = result.user;
            setUser(user)
        })
    }

    return (
        <div>
            { user ? 
           <div> 
               <h4>{user.displayName}</h4>
               <form action="">
                   <input type="text" placeholder="adınız" value={name} onChange={(e)=> setName(e.target.value)} />
                   <input type="password" placeholder="adınız" value={password} onChange={(e)=> setPassword(e.target.value)} />
                   <button onClick={add}>Kullanıcı Gönder</button>
               </form>
               <button onClick={() => auth.signOut()}>Çıkış Yap</button> 
            </div> : 
            <button type="button" onClick={login} className="btn btn primary">Google ile Kayıt Ol</button>
            }
        </div>
    )
}
