import React, {useRef, useState, useEffect} from 'react'
import { auth, provider } from '../firebase';
import { getAuth, signInWithPopup, GoogleAuthProvider } from "firebase/auth";
import {db} from '../firebase'

export default function Signup() {

    const [name,setName] = useState("");
    const [surname,setSurname] = useState("");
    const [user,setUser] = useState(null);

    const add = (e) => {
        e.preventDefault()

        db.collection("users").add({
            name : name,
            surname : surname
        })
        setName("");
        setSurname("");
    }

    useEffect(() => {
        getAuth().onAuthStateChanged(authUser => {
            if(authUser){
                setUser(authUser)
            }else{
                setUser(null)
            }
        }) 
    }, [])

    const login = () => {
        const auth = getAuth();
        signInWithPopup(auth,provider)
        .then((result) => {
            const credential = GoogleAuthProvider.credentialFromResult(result);
            const token = credential.accessToken;
            const user = result.user;
            setUser(user)
        })
    }

    return (
        <div>
            { user ? 
           <div> 
               <h4>{user.displayName}</h4>
               <form action="">
                   <input type="text" placeholder="adınız" value={name} onChange={(e)=> setName(e.target.value)} />
                   <input type="text" placeholder="adınız" value={surname} onChange={(e)=> setSurname(e.target.value)} />
                   <button onClick={add}>Kullanıcı Gönder</button>
               </form>
               <button onClick={() => auth.signOut()}>Çıkış Yap</button> 
            </div> : 
            <button type="button" onClick={login} className="btn btn primary">Google ile Kayıt Ol</button>
            }
        </div>
    )
}
